{
	"query":         "Select [System.Id], [System.Title], [System.State]         From WorkItems         Where [System.CreatedDate] < '2018-11-01'             AND [System.State] = 'Committed'             AND [System.WorkItemType] ='Product Backlog Item'             AND [Microsoft.VSTS.Common.StateChangeDate] >= '2018-11-15'         order by [Microsoft.VSTS.Common.Priority] asc,             [System.CreatedDate] desc         ASOF '2018-11-21'"
   }

{
  "query": "Select [System.Id], [System.Title], [System.State] From WorkItems Where [System.CreatedDate] < '2018-08-29' AND [System.State] = 'Committed' AND [System.WorkItemType] = '{{story_work_item_type}}' AND [Microsoft.VSTS.Common.StateChangeDate] >= '2018-08-29' order by [Microsoft.VSTS.Common.Priority] asc, [System.CreatedDate] desc  ASOF '2018-09-05'"
}

{
  "query": "Select [System.Id] FROM WorkItems WHERE [System.TeamProject] = @project AND [System.WorkItemType] = '{{story_work_item_type}}' AND ([System.State] = 'New' OR [System.State] = 'Approved' OR [System.State] = 'Committed') AND [WEF_7FD0B1E64F5C422C97B804850CD7B18F_Kanban.Column] != '5. Ready for Environments Team'"
}

{
        "query": "Select [System.Id] FROM WorkItems WHERE [System.TeamProject] = 'Project Name'         AND [System.WorkItemType] = 'Product Backlog Item' AND ([System.State] = 'New' OR [System.State] = 'Approved' OR [System.State] = 'Committed') AND [WEF_7FD0B1E64F5C422C97B804850CD7B18F_Kanban.Column] = '5. Ready for Environments Team'    ASOF '2018-08-29' "
        } `

{
        "query": "Select [System.Id] FROM WorkItems WHERE [System.TeamProject] = 'Project Name'         AND [System.WorkItemType] = 'Product Backlog Item' AND ([System.State] = 'New' OR [System.State] = 'Approved' OR [System.State] = 'Committed') AND [WEF_7FD0B1E64F5C422C97B804850CD7B18F_Kanban.Column] = '5. Ready for Environments Team'"
        } `

{
"query": "Select [System.Id] FROM WorkItems WHERE [System.State] = 'Committed' AND [System.TeamProject] = 'Project Name' AND [System.WorkItemType] = '{{story_work_item_type}}' ASOF '2018-10-29'"
}

{
  "query": "Select [System.Title] FROM WorkItemLinks WHERE Source.[System.WorkItemType] = 'Feature' AND Target.[System.WorkItemType] = '{{story_work_item_type}}' AND [System.Links.LinkType] = 'System.LinkTypes.Hierarchy-Forward' ORDER BY [Microsoft.VSTS.Common.BacklogPriority] ASC,[System.Id] ASC MODE (Recursive, ReturnMatchingChildren)"
}

