  "query": "Select [System.Id] FROM WorkItems WHERE [System.TeamProject] = 'Project Name' AND [System.WorkItemType] = '{{story_work_item_type}}' AND ([System.State] = 'Committed' OR [System.State] = 'Done') ASOF '2018-10-29'"
}

